     1→import { useState } from "react";
     2→import { useForm } from "react-hook-form";
     3→import { zodResolver } from "@hookform/resolvers/zod";
     4→import { z } from "zod";
     5→import { Link } from "@tanstack/react-router";
     6→import { Eye, EyeOff, Loader2, Mail, Lock } from "lucide-react";
     7→import { Button } from "@/components/ui/button";
     8→import { Input } from "@/components/ui/input";
     9→import { Label } from "@/components/ui/label";
    10→import { useAuth } from "@/lib/auth";
    11→
    12→const loginSchema = z.object({
    13→  email: z.string().email("Please enter a valid email address"),
    14→  password: z.string().min(1, "Password is required"),
    15→});
    16→
    17→type LoginFormData = z.infer<typeof loginSchema>;
    18→
    19→interface LoginFormProps {
    20→  onSuccess?: () => void;
    21→}
    22→
    23→export function LoginForm({ onSuccess }: LoginFormProps) {
    24→  const { loginLocal } = useAuth();
    25→  const [showPassword, setShowPassword] = useState(false);
    26→  const [error, setError] = useState<string | null>(null);
    27→
    28→  const {
    29→    register,
    30→    handleSubmit,
    31→    formState: { errors, isSubmitting },
    32→  } = useForm<LoginFormData>({
    33→    resolver: zodResolver(loginSchema),
    34→  });
    35→
    36→  const onSubmit = async (data: LoginFormData) => {
    37→    setError(null);
    38→    try {
    39→      await loginLocal(data.email, data.password);
    40→      onSuccess?.();
    41→    } catch (err) {
    42→      setError(err instanceof Error ? err.message : "Login failed. Please try again.");
    43→    }
    44→  };
    45→
    46→  return (
    47→    <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
    48→      {error && (
    49→        <div className="p-3 rounded-lg bg-destructive/10 border border-destructive/20 text-destructive text-sm">
    50→          {error}
    51→        </div>
    52→      )}
    53→
    54→      <div className="space-y-2">
    55→        <Label htmlFor="email">
    56→          Email
    57→        </Label>
    58→        <div className="relative">
    59→          <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
    60→          <Input
    61→            id="email"
    62→            type="email"
    63→            placeholder="you@example.com"
    64→            autoComplete="email"
    65→            className="pl-10"
    66→            {...register("email")}
    67→          />
    68→        </div>
    69→        {errors.email && (
    70→          <p className="text-sm text-destructive">{errors.email.message}</p>
    71→        )}
    72→      </div>
    73→
    74→      <div className="space-y-2">
    75→        <div className="flex items-center justify-between">
    76→          <Label htmlFor="password">
    77→            Password
    78→          </Label>
    79→          <Link
    80→            to="/forgot-password"
    81→            className="text-sm text-primary hover:text-primary/80 transition-colors"
    82→          >
    83→            Forgot password?
    84→          </Link>
    85→        </div>
    86→        <div className="relative">
    87→          <Lock className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
    88→          <Input
    89→            id="password"
    90→            type={showPassword ? "text" : "password"}
    91→            placeholder="Enter your password"
    92→            autoComplete="current-password"
    93→            className="pl-10 pr-10"
    94→            {...register("password")}
    95→          />
    96→          <button
    97→            type="button"
    98→            onClick={() => setShowPassword(!showPassword)}
    99→            className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
   100→          >
   101→            {showPassword ? (
   102→              <EyeOff className="w-5 h-5" />
   103→            ) : (
   104→              <Eye className="w-5 h-5" />
   105→            )}
   106→          </button>
   107→        </div>
   108→        {errors.password && (
   109→          <p className="text-sm text-destructive">{errors.password.message}</p>
   110→        )}
   111→      </div>
   112→
   113→      <Button
   114→        type="submit"
   115→        disabled={isSubmitting}
   116→        className="w-full h-11 font-semibold shadow-lg shadow-primary/25 transition-all duration-300"
   117→      >
   118→        {isSubmitting ? (
   119→          <>
   120→            <Loader2 className="w-4 h-4 mr-2 animate-spin" />
   121→            Signing in...
   122→          </>
   123→        ) : (
   124→          "Sign In"
   125→        )}
   126→      </Button>
   127→    </form>
   128→  );
   129→}
