     1→import {
     2→  createContext,
     3→  useCallback,
     4→  useEffect,
     5→  useState,
     6→  type ReactNode,
     7→} from "react";
     8→import type { User } from "oidc-client-ts";
     9→import { getUserManager } from "./oidc-config";
    10→import { parseUser } from "./parse-user";
    11→import { parseLocalUser } from "./parse-local-user";
    12→import {
    13→  clearLocalTokens,
    14→  getLocalTokens,
    15→  isLocalTokenExpired,
    16→  storeLocalTokens,
    17→  updateAccessToken,
    18→} from "./local-storage";
    19→import {
    20→  loginLocal as loginLocalApi,
    21→  logout as logoutLocalApi,
    22→  refreshAccessToken,
    23→  register as registerApi,
    24→} from "@/lib/api/auth";
    25→import type {
    26→  AuthContextType,
    27→  AuthProvider as AuthProviderType,
    28→  AuthUser,
    29→  RegisterData,
    30→} from "@/types/auth";
    31→
    32→export const AuthContext = createContext<AuthContextType | null>(null);
    33→
    34→interface AuthProviderProps {
    35→  children: ReactNode;
    36→}
    37→
    38→export function AuthProvider({ children }: AuthProviderProps) {
    39→  const [user, setUser] = useState<AuthUser | null>(null);
    40→  const [isLoading, setIsLoading] = useState(true);
    41→  const [authProvider, setAuthProvider] = useState<AuthProviderType | null>(null);
    42→
    43→  const userManager = getUserManager();
    44→
    45→  // OIDC event handlers
    46→  const handleUserLoaded = useCallback((oidcUser: User) => {
    47→    setUser(parseUser(oidcUser));
    48→    setAuthProvider("oidc");
    49→    setIsLoading(false);
    50→  }, []);
    51→
    52→  const handleUserUnloaded = useCallback(() => {
    53→    setUser(null);
    54→    setAuthProvider(null);
    55→    setIsLoading(false);
    56→  }, []);
    57→
    58→  // Initialize: Check for both OIDC and local tokens
    59→  useEffect(() => {
    60→    // Register OIDC event handlers
    61→    userManager.events.addUserLoaded(handleUserLoaded);
    62→    userManager.events.addUserUnloaded(handleUserUnloaded);
    63→    userManager.events.addSilentRenewError(() => {
    64→      console.error("Silent renew error");
    65→      if (authProvider === "oidc") {
    66→        setUser(null);
    67→        setAuthProvider(null);
    68→      }
    69→    });
    70→    userManager.events.addAccessTokenExpired(() => {
    71→      console.warn("Access token expired");
    72→      if (authProvider === "oidc") {
    73→        setUser(null);
    74→        setAuthProvider(null);
    75→      }
    76→    });
    77→
    78→    // Check for existing authentication
    79→    const initAuth = async () => {
    80→      // 1. Check for OIDC user first
    81→      try {
    82→        const oidcUser = await userManager.getUser();
    83→        if (oidcUser && !oidcUser.expired) {
    84→          setUser(parseUser(oidcUser));
    85→          setAuthProvider("oidc");
    86→          setIsLoading(false);
    87→          return;
    88→        }
    89→      } catch (error) {
    90→        console.error("Failed to get OIDC user:", error);
    91→      }
    92→
    93→      // 2. Check for local tokens
    94→      const tokens = getLocalTokens();
    95→      if (tokens) {
    96→        if (!isLocalTokenExpired(tokens)) {
    97→          // Token is valid, parse and set user
    98→          const parsedUser = parseLocalUser(tokens.accessToken);
    99→          if (parsedUser) {
   100→            setUser(parsedUser);
   101→            setAuthProvider("local");
   102→            setIsLoading(false);
   103→            return;
   104→          }
   105→        } else if (tokens.refreshToken) {
   106→          // Try to refresh the token
   107→          try {
   108→            const response = await refreshAccessToken(tokens.refreshToken);
   109→            updateAccessToken(response.access_token, response.expires_in);
   110→            const parsedUser = parseLocalUser(response.access_token);
   111→            if (parsedUser) {
   112→              setUser(parsedUser);
   113→              setAuthProvider("local");
   114→              setIsLoading(false);
   115→              return;
   116→            }
   117→          } catch (error) {
   118→            console.error("Failed to refresh token:", error);
   119→            clearLocalTokens();
   120→          }
   121→        }
   122→      }
   123→
   124→      // No valid auth found
   125→      setIsLoading(false);
   126→    };
   127→
   128→    initAuth();
   129→
   130→    return () => {
   131→      userManager.events.removeUserLoaded(handleUserLoaded);
   132→      userManager.events.removeUserUnloaded(handleUserUnloaded);
   133→    };
   134→  }, [userManager, handleUserLoaded, handleUserUnloaded, authProvider]);
   135→
   136→  // OIDC Login (redirect to Keycloak)
   137→  const login = useCallback(async () => {
   138→    try {
   139→      await userManager.signinRedirect();
   140→    } catch (error) {
   141→      console.error("OIDC login failed:", error);
   142→      throw error;
   143→    }
   144→  }, [userManager]);
   145→
   146→  // Local Login (username/password)
   147→  const loginLocal = useCallback(async (email: string, password: string) => {
   148→    try {
   149→      const response = await loginLocalApi(email, password);
   150→      storeLocalTokens(response);
   151→
   152→      const parsedUser = parseLocalUser(response.access_token);
   153→      if (parsedUser) {
   154→        setUser(parsedUser);
   155→        setAuthProvider("local");
   156→      } else {
   157→        throw new Error("Failed to parse user from token");
   158→      }
   159→    } catch (error) {
   160→      console.error("Local login failed:", error);
   161→      throw error;
   162→    }
   163→  }, []);
   164→
   165→  // Register new user
   166→  const register = useCallback(async (data: RegisterData) => {
   167→    try {
   168→      await registerApi(data);
   169→      // Registration successful - user needs to verify email
   170→    } catch (error) {
   171→      console.error("Registration failed:", error);
   172→      throw error;
   173→    }
   174→  }, []);
   175→
   176→  // Unified logout
   177→  const logout = useCallback(async () => {
   178→    try {
   179→      if (authProvider === "oidc") {
   180→        await userManager.signoutRedirect();
   181→      } else if (authProvider === "local") {
   182→        const tokens = getLocalTokens();
   183→        if (tokens) {
   184→          try {
   185→            await logoutLocalApi(tokens.refreshToken, tokens.accessToken);
   186→          } catch (e) {
   187→            // Ignore logout API errors - we'll clear tokens anyway
   188→            console.error("Logout API error:", e);
   189→          }
   190→        }
   191→        clearLocalTokens();
   192→        setUser(null);
   193→        setAuthProvider(null);
   194→      }
   195→    } catch (error) {
   196→      console.error("Logout failed:", error);
   197→      // Clear local state even if API call fails
   198→      clearLocalTokens();
   199→      setUser(null);
   200→      setAuthProvider(null);
   201→      throw error;
   202→    }
   203→  }, [userManager, authProvider]);
   204→
   205→  // Get access token (works for both OIDC and local)
   206→  const getAccessToken = useCallback(async (): Promise<string | null> => {
   207→    try {
   208→      if (authProvider === "oidc") {
   209→        const oidcUser = await userManager.getUser();
   210→        return oidcUser?.access_token ?? null;
   211→      }
   212→
   213→      if (authProvider === "local") {
   214→        let tokens = getLocalTokens();
   215→
   216→        if (!tokens) return null;
   217→
   218→        // Check if token needs refresh
   219→        if (isLocalTokenExpired(tokens)) {
   220→          if (tokens.refreshToken) {
   221→            try {
   222→              const response = await refreshAccessToken(tokens.refreshToken);
   223→              updateAccessToken(response.access_token, response.expires_in);
   224→              return response.access_token;
   225→            } catch (error) {
   226→              console.error("Failed to refresh token:", error);
   227→              // Token refresh failed, clear auth state
   228→              clearLocalTokens();
   229→              setUser(null);
   230→              setAuthProvider(null);
   231→              return null;
   232→            }
   233→          }
   234→          return null;
   235→        }
   236→
   237→        return tokens.accessToken;
   238→      }
   239→
   240→      return null;
   241→    } catch (error) {
   242→      console.error("Failed to get access token:", error);
   243→      return null;
   244→    }
   245→  }, [userManager, authProvider]);
   246→
   247→  const value: AuthContextType = {
   248→    isAuthenticated: !!user,
   249→    isLoading,
   250→    user,
   251→    authProvider,
   252→    login,
   253→    logout,
   254→    getAccessToken,
   255→    loginLocal,
   256→    register,
   257→  };
   258→
   259→  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
   260→}
   261→

