     1→/**
     2→ * Local token storage utilities.
     3→ *
     4→ * Handles storage and retrieval of local auth tokens in sessionStorage.
     5→ * Uses sessionStorage for better security (cleared when browser closes).
     6→ */
     7→
     8→import type { LocalTokens, LoginResponse } from "@/types/auth";
     9→
    10→const STORAGE_KEY = "local_auth_tokens";
    11→
    12→/**
    13→ * Store local auth tokens.
    14→ */
    15→export function storeLocalTokens(response: LoginResponse): void {
    16→  const tokens: LocalTokens = {
    17→    accessToken: response.access_token,
    18→    refreshToken: response.refresh_token,
    19→    expiresAt: Date.now() + response.expires_in * 1000,
    20→  };
    21→
    22→  try {
    23→    sessionStorage.setItem(STORAGE_KEY, JSON.stringify(tokens));
    24→  } catch (e) {
    25→    console.error("Failed to store tokens:", e);
    26→  }
    27→}
    28→
    29→/**
    30→ * Get stored local auth tokens.
    31→ */
    32→export function getLocalTokens(): LocalTokens | null {
    33→  try {
    34→    const stored = sessionStorage.getItem(STORAGE_KEY);
    35→    if (!stored) return null;
    36→
    37→    return JSON.parse(stored) as LocalTokens;
    38→  } catch (e) {
    39→    console.error("Failed to get tokens:", e);
    40→    return null;
    41→  }
    42→}
    43→
    44→/**
    45→ * Clear stored local auth tokens.
    46→ */
    47→export function clearLocalTokens(): void {
    48→  try {
    49→    sessionStorage.removeItem(STORAGE_KEY);
    50→  } catch (e) {
    51→    console.error("Failed to clear tokens:", e);
    52→  }
    53→}
    54→
    55→/**
    56→ * Check if the access token is expired or will expire soon.
    57→ * Returns true if token is expired or expires within the buffer time.
    58→ */
    59→export function isLocalTokenExpired(
    60→  tokens: LocalTokens | null,
    61→  bufferSeconds: number = 60
    62→): boolean {
    63→  if (!tokens) return true;
    64→
    65→  const bufferMs = bufferSeconds * 1000;
    66→  return tokens.expiresAt - bufferMs <= Date.now();
    67→}
    68→
    69→/**
    70→ * Update the stored access token after a refresh.
    71→ */
    72→export function updateAccessToken(accessToken: string, expiresIn: number): void {
    73→  const tokens = getLocalTokens();
    74→  if (!tokens) return;
    75→
    76→  const updatedTokens: LocalTokens = {
    77→    ...tokens,
    78→    accessToken,
    79→    expiresAt: Date.now() + expiresIn * 1000,
    80→  };
    81→
    82→  try {
    83→    sessionStorage.setItem(STORAGE_KEY, JSON.stringify(updatedTokens));
    84→  } catch (e) {
    85→    console.error("Failed to update tokens:", e);
    86→  }
    87→}
    88→
    89→/**
    90→ * Parse a JWT token and extract payload (without verification).
    91→ * Used for reading claims from local tokens.
    92→ */
    93→export function parseJwtPayload(token: string): Record<string, unknown> | null {
    94→  try {
    95→    const parts = token.split(".");
    96→    if (parts.length !== 3) return null;
    97→
    98→    const payload = parts[1];
    99→    const decoded = atob(payload.replace(/-/g, "+").replace(/_/g, "/"));
   100→    return JSON.parse(decoded);
   101→  } catch (e) {
   102→    console.error("Failed to parse JWT:", e);
   103→    return null;
   104→  }
   105→}
