     1→import { StrictMode } from "react";
     2→import { createRoot } from "react-dom/client";
     3→import { QueryClientProvider } from "@tanstack/react-query";
     4→import { ReactQueryDevtools } from "@tanstack/react-query-devtools";
     5→import { RouterProvider, createRouter } from "@tanstack/react-router";
     6→import { AuthProvider, useAuth } from "@/lib/auth";
     7→import { WebSocketProvider } from "@/lib/websocket";
     8→import { ThemeProvider } from "@/components/providers/theme-provider";
     9→import { DynamicTheme } from "@/components/providers/dynamic-theme";
    10→import { queryClient } from "@/lib/api";
    11→import { routeTree } from "./routeTree.gen";
    12→import "./index.css";
    13→
    14→// Create router instance
    15→const router = createRouter({
    16→  routeTree,
    17→  context: {
    18→    auth: undefined!,
    19→    queryClient,
    20→  },
    21→  defaultPreload: "intent",
    22→  defaultPreloadStaleTime: 0,
    23→});
    24→
    25→// Register router for type safety
    26→declare module "@tanstack/react-router" {
    27→  interface Register {
    28→    router: typeof router;
    29→  }
    30→}
    31→
    32→function InnerApp() {
    33→  const auth = useAuth();
    34→  return (
    35→    <WebSocketProvider>
    36→      <RouterProvider router={router} context={{ auth, queryClient }} />
    37→    </WebSocketProvider>
    38→  );
    39→}
    40→
    41→function App() {
    42→  return (
    43→    <ThemeProvider defaultTheme="system" storageKey="theme">
    44→      <QueryClientProvider client={queryClient}>
    45→        <DynamicTheme>
    46→          <AuthProvider>
    47→            <InnerApp />
    48→          </AuthProvider>
    49→        </DynamicTheme>
    50→        {import.meta.env.DEV && <ReactQueryDevtools buttonPosition="bottom-left" />}
    51→      </QueryClientProvider>
    52→    </ThemeProvider>
    53→  );
    54→}
    55→
    56→createRoot(document.getElementById("root")!).render(
    57→  <StrictMode>
    58→    <App />
