     1→/**
     2→ * Color conversion utilities for dynamic theming.
     3→ * Converts hex colors to OKLCh color space for CSS variables.
     4→ */
     5→
     6→/**
     7→ * Convert hex color to RGB values.
     8→ */
     9→function hexToRgb(hex: string): { r: number; g: number; b: number } {
    10→  const cleaned = hex.replace("#", "");
    11→  const r = parseInt(cleaned.slice(0, 2), 16) / 255;
    12→  const g = parseInt(cleaned.slice(2, 4), 16) / 255;
    13→  const b = parseInt(cleaned.slice(4, 6), 16) / 255;
    14→  return { r, g, b };
    15→}
    16→
    17→/**
    18→ * Convert linear RGB to OKLab.
    19→ */
    20→function linearRgbToOklab(r: number, g: number, b: number): { L: number; a: number; b: number } {
    21→  // Convert sRGB to linear RGB
    22→  const toLinear = (c: number) => (c <= 0.04045 ? c / 12.92 : Math.pow((c + 0.055) / 1.055, 2.4));
    23→  const lr = toLinear(r);
    24→  const lg = toLinear(g);
    25→  const lb = toLinear(b);
    26→
    27→  // Convert to OKLab
    28→  const l_ = Math.cbrt(0.4122214708 * lr + 0.5363325363 * lg + 0.0514459929 * lb);
    29→  const m_ = Math.cbrt(0.2119034982 * lr + 0.6806995451 * lg + 0.1073969566 * lb);
    30→  const s_ = Math.cbrt(0.0883024619 * lr + 0.2817188376 * lg + 0.6299787005 * lb);
    31→
    32→  return {
    33→    L: 0.2104542553 * l_ + 0.793617785 * m_ - 0.0040720468 * s_,
    34→    a: 1.9779984951 * l_ - 2.428592205 * m_ + 0.4505937099 * s_,
    35→    b: 0.0259040371 * l_ + 0.7827717662 * m_ - 0.808675766 * s_,
    36→  };
    37→}
    38→
    39→/**
    40→ * Convert OKLab to OKLCh.
    41→ */
    42→function oklabToOklch(L: number, a: number, b: number): { L: number; C: number; h: number } {
    43→  const C = Math.sqrt(a * a + b * b);
    44→  let h = (Math.atan2(b, a) * 180) / Math.PI;
    45→  if (h < 0) h += 360;
    46→  return { L, C, h };
    47→}
    48→
    49→/**
    50→ * Convert hex color to OKLCh CSS value.
    51→ * @param hex - Hex color string (e.g., "#10b981")
    52→ * @returns OKLCh CSS value (e.g., "oklch(0.696 0.17 162.48)")
    53→ */
    54→export function hexToOklch(hex: string): string {
    55→  const { r, g, b } = hexToRgb(hex);
    56→  const lab = linearRgbToOklab(r, g, b);
    57→  const lch = oklabToOklch(lab.L, lab.a, lab.b);
    58→
    59→  // Format to 3 decimal places
    60→  const L = lch.L.toFixed(3);
    61→  const C = lch.C.toFixed(3);
    62→  const h = lch.h.toFixed(3);
    63→
    64→  return `oklch(${L} ${C} ${h})`;
    65→}
    66→
    67→/**
    68→ * Generate a lighter/darker variant of a color.
    69→ * @param hex - Base hex color
    70→ * @param lightnessOffset - Amount to adjust lightness (-1 to 1)
    71→ */
    72→export function adjustLightness(hex: string, lightnessOffset: number): string {
    73→  const { r, g, b } = hexToRgb(hex);
    74→  const lab = linearRgbToOklab(r, g, b);
    75→  const lch = oklabToOklch(lab.L, lab.a, lab.b);
    76→
    77→  // Adjust lightness, clamping to valid range
    78→  const newL = Math.max(0, Math.min(1, lch.L + lightnessOffset));
    79→
    80→  return `oklch(${newL.toFixed(3)} ${lch.C.toFixed(3)} ${lch.h.toFixed(3)})`;
    81→}
    82→
    83→/**
    84→ * Generate primary color foreground (text color for buttons).
    85→ * Returns white or black depending on primary color brightness.
    86→ */
    87→export function getPrimaryForeground(hex: string): string {
    88→  const { r, g, b } = hexToRgb(hex);
    89→  // Use relative luminance formula
    90→  const luminance = 0.2126 * r + 0.7152 * g + 0.0722 * b;
    91→  // Return white for dark colors, dark for light colors
    92→  return luminance < 0.5 ? "oklch(0.98 0 0)" : "oklch(0.15 0 0)";
    93→}
