     1→import type { User } from "oidc-client-ts";
     2→import type { AuthUser } from "@/types/auth";
     3→
     4→interface TokenClaims {
     5→  sub?: string;
     6→  email?: string;
     7→  name?: string;
     8→  preferred_username?: string;
     9→  roles?: string[];
    10→  realm_access?: {
    11→    roles?: string[];
    12→  };
    13→  resource_access?: {
    14→    api?: {
    15→      roles?: string[];
    16→    };
    17→    [key: string]: { roles?: string[] } | undefined;
    18→  };
    19→  org_id?: string;
    20→  team_ids?: string[];
    21→}
    22→
    23→export function parseUser(oidcUser: User): AuthUser {
    24→  const profile = oidcUser.profile as TokenClaims;
    25→
    26→  // Extract realm roles
    27→  const realmRoles = profile.realm_access?.roles ?? [];
    28→
    29→  // Extract client roles from 'roles' claim (Keycloak maps client roles there)
    30→  // or from resource_access.api.roles
    31→  const clientRoles =
    32→    profile.roles ??
    33→    profile.resource_access?.api?.roles ??
    34→    [];
    35→
    36→  return {
    37→    sub: profile.sub ?? "",
    38→    email: profile.email ?? "",
    39→    name: profile.name ?? profile.preferred_username ?? "",
    40→    preferred_username: profile.preferred_username,
    41→    realmRoles,
    42→    clientRoles,
    43→    orgId: profile.org_id,
    44→    teamIds: profile.team_ids,
    45→  };
    46→}
    47→
    48→export function hasRole(user: AuthUser | null, roles: string[]): boolean {
    49→  if (!user) return false;
    50→
    51→  const userRoles = [...user.realmRoles, ...user.clientRoles];
    52→  return roles.some((role) => userRoles.includes(role));
    53→}
    54→
    55→export function hasAnyRole(user: AuthUser | null, roles: string[]): boolean {
    56→  return hasRole(user, roles);
    57→}
    58→
    59→export function hasAllRoles(user: AuthUser | null, roles: string[]): boolean {
    60→  if (!user) return false;
    61→
    62→  const userRoles = [...user.realmRoles, ...user.clientRoles];
    63→  return roles.every((role) => userRoles.includes(role));
    64→}
