     1→import { useState, useEffect } from "react";
     2→
     3→export function useDebounce<T>(value: T, delay: number = 300): T {
     4→  // State to hold the debounced value
     5→  const [debouncedValue, setDebouncedValue] = useState<T>(value);
     6→
     7→  useEffect(() => {
     8→    // Set up a timer to update the debounced value after the delay
     9→    const timer = setTimeout(() => {
    10→      setDebouncedValue(value);
    11→    }, delay);
    12→
    13→    // Clean up the timer if value changes or component unmounts
    14→    return () => {
    15→      clearTimeout(timer);
    16→    };
    17→  }, [value, delay]);
    18→
    19→  return debouncedValue;
    20→}
    21→

