     1→import { useState, useEffect, useCallback } from "react";
     2→
     3→export function useLocalStorage<T>(
     4→  key: string,
     5→  initialValue: T
     6→): [T, (value: T | ((prev: T) => T)) => void] {
     7→  // Get initial value from localStorage or use the provided initial value
     8→  const [storedValue, setStoredValue] = useState<T>(() => {
     9→    if (typeof window === "undefined") {
    10→      return initialValue;
    11→    }
    12→    try {
    13→      const item = window.localStorage.getItem(key);
    14→      return item ? JSON.parse(item) : initialValue;
    15→    } catch (error) {
    16→      console.warn(`Error reading localStorage key "${key}":`, error);
    17→      return initialValue;
    18→    }
    19→  });
    20→
    21→  // Update localStorage when value changes
    22→  useEffect(() => {
    23→    if (typeof window === "undefined") {
    24→      return;
    25→    }
    26→    try {
    27→      window.localStorage.setItem(key, JSON.stringify(storedValue));
    28→    } catch (error) {
    29→      console.warn(`Error setting localStorage key "${key}":`, error);
    30→    }
    31→  }, [key, storedValue]);
    32→
    33→  const setValue = useCallback(
    34→    (value: T | ((prev: T) => T)) => {
    35→      setStoredValue((prev) => {
    36→        const newValue = value instanceof Function ? value(prev) : value;
    37→        return newValue;
    38→      });
    39→    },
    40→    []
    41→  );
    42→
    43→  return [storedValue, setValue];
    44→}
