     8→/**
     9→ * Custom render options that extend RTL's RenderOptions
    10→ */
    11→interface CustomRenderOptions extends Omit<RenderOptions, "wrapper"> {
    12→  /**
    13→   * Custom auth context to provide to components
    14→   * If not provided, defaults to unauthenticated state
    15→   */
    16→  authContext?: Partial<AuthContextType>;
    17→  /**
    18→   * Custom QueryClient instance
    19→   * If not provided, creates a new one with test-friendly defaults
    20→   */
    21→  queryClient?: QueryClient;
    22→}
    23→
    24→/**
    25→ * Creates a test-friendly QueryClient with disabled retries and cache
    26→ * to make tests more predictable and faster
    27→ */
    28→function createTestQueryClient(): QueryClient {
    29→  return new QueryClient({
    30→    defaultOptions: {
    31→      queries: {
    32→        retry: false,
    33→        gcTime: 0,
    34→        staleTime: 0,
    35→        refetchOnWindowFocus: false,
    36→        refetchOnReconnect: false,
    37→      },
    38→      mutations: {
    39→        retry: false,
    40→      },
    41→    },
    42→    logger: {
    43→      log: () => {},
    44→      warn: () => {},
    45→      error: () => {},
    46→    },
    47→  });
    48→}
    49→
    50→/**
    51→ * Wrapper component that provides all necessary context providers for testing
    52→ */
    53→function AllProviders({
    54→  children,
    55→  authContext,
    56→  queryClient,
    57→}: {
    58→  children: ReactNode;
    59→  authContext: AuthContextType;
    60→  queryClient: QueryClient;
    61→}) {
    62→  return (
    63→    <QueryClientProvider client={queryClient}>
    64→      <AuthContext.Provider value={authContext}>
    65→        {children}
    66→      </AuthContext.Provider>
    67→    </QueryClientProvider>
    68→  );
    69→}
    70→
    71→/**
    72→ * Custom render function that wraps components with necessary providers
    73→ *
    74→ * @example
    75→ * ```tsx
    76→ * // Render with authenticated user
    77→ * const { getByText } = renderWithProviders(<MyComponent />, {
    78→ *   authContext: {
    79→ *     user: createMockUser({ email: 'test@example.com' }),
    80→ *     isAuthenticated: true,
    81→ *   }
    82→ * });
    83→ *
    84→ * // Render with unauthenticated state (default)
    85→ * const { getByText } = renderWithProviders(<MyComponent />);
    86→ * ```
    87→ */
    88→export function renderWithProviders(
    89→  ui: ReactElement,
    90→  options: CustomRenderOptions = {}
    91→) {
    92→  const { authContext: customAuthContext, queryClient: customQueryClient, ...renderOptions } = options;
    93→
    94→  // Create test QueryClient if not provided
    95→  const queryClient = customQueryClient ?? createTestQueryClient();
    96→
    97→  // Create mock auth context with custom overrides
    98→  const authContext = createMockAuthContext(customAuthContext);
    99→
   100→  // Render with all providers
   101→  const result = render(ui, {
   102→    wrapper: ({ children }) => (
   103→      <AllProviders authContext={authContext} queryClient={queryClient}>
   104→        {children}
   105→      </AllProviders>
   106→    ),
   107→    ...renderOptions,
   108→  });
   109→
   110→  return {
   111→    ...result,
   112→    queryClient,
   113→    authContext,
   114→  };
   115→}
